INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (372, 56, 127, 90, 'Blade Mail can deal huge damage to Clinkz and it has no way to heal himself in a fight or protect against this damage with Black King Bar. Due to his high attack speed and range, Clinkz can have <b>3-4</b> attack projectiles flying at once, which are easy to reflect with Blade Mail.', 1);
INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (194, 10, 196, 80, 'Spirit Vessel reduces the health Morphling gains using Attribute Shift by <b>60%</b>.', 1);
INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (524, 103, 96, 90, 'Turns a target unit into a harmless critter for <b>3.5</b> seconds. The target has a base movement speed of <b>140</b> and will be silenced, muted, and disarmed.', 1);
INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (527, 103, 250, 65, 'Silences a target for <b>5</b> seconds. At the end of the silence, an additional <b>30%</b> of all damage taken during the silence will be dealt to the target as magical damage.', 1);
INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (528, 103, 229, 50, 'When cast on an ally, grants them <b>6</b> armor, <b>50</b>attack speed, and <b>10%</b> movement speed. When cast on an enemy, removes <b>6</b> armor, <b>50%</b> attack speed, and <b>10%</b> movement speed.', 1);
INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (525, 103, 119, 80, 'Emits a freezing wave that deals <b>200</b> magical damage to enemies and slows their movement by <b>40%</b> for <b>4</b> seconds.', 1);
INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (627, 99, 249, 100, 'Makes you invisible for 14 seconds, or until you attack or cast a spell. While invisible, you move 25% faster and can move through units.', 1);
INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (628, 99, 225, 95, 'Dispels the target and applies a debuff for 5 seconds. Continuously dispels the target. Anytime the target is attacked, it will be slowed by 80% for 0.5 seconds.', 1);
INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (617, 32, 40, 100, 'Dust of Appearance not only reveals Riki, but also slows his movement speed which put him in a very vulnerable state since he cannot turn off  Cloak and Dagger.', 1);
INSERT INTO public.item_vs_hero_match (id, id_hero, id_item, score, description, social_score) VALUES (618, 32, 30, 90, 'Gem of True Sight is a nightmare for Riki, heavily discouraging him to go roam in dark area or follow an enemy.', 2);